import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTimes, FaBars } from 'react-icons/fa';
import logo from './assets/logo.png';
import './CheckoutPage.css';

export default function CheckoutPage({ cart, subtotal, shippingCost, total }) {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    zipCode: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [isFormValid, setIsFormValid] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submissionMessage, setSubmissionMessage] = useState(''); // State for success/error message

  const validateForm = useCallback(() => {
    let errors = {};
    let valid = true;

    if (!formData.firstName.trim()) {
      errors.firstName = 'First Name is required';
      valid = false;
    }
    if (!formData.lastName.trim()) {
      errors.lastName = 'Last Name is required';
      valid = false;
    }
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
      valid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email address is invalid';
      valid = false;
    }
    if (!formData.phone.trim()) {
      errors.phone = 'Phone number is required';
      valid = false;
    }
    if (!formData.address.trim()) {
      errors.address = 'Address is required';
      valid = false;
    }
    if (!formData.city.trim()) {
      errors.city = 'City is required';
      valid = false;
    }
    if (!formData.zipCode.trim()) {
      errors.zipCode = 'Zip Code is required';
      valid = false;
    }

    if (cart.length === 0) {
      valid = false; // Cart must not be empty
    }

    setFormErrors(errors);
    setIsFormValid(valid);
    return valid;
  }, [formData, cart]);

  useEffect(() => {
    validateForm();
  }, [formData, cart, validateForm]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission
    setSubmissionMessage(''); // Clear any previous messages

    if (!validateForm()) {
      setSubmissionMessage('Please correct the errors in the form.');
      return;
    }

    if (cart.length === 0) {
      setSubmissionMessage('Your cart is empty. Please add items to proceed.');
      return;
    }

    setIsSubmitting(true);
    try {
      const orderDetails = {
        ...formData,
        cartItems: cart.map(item => ({
          title: item.title,
          quantity: item.quantity,
          price: item.price,
          selectedSize: item.selectedSize,
          selectedColor: item.selectedColor
        })),
        subtotal: subtotal.toFixed(2),
        shippingCost: shippingCost.toFixed(2),
        total: total.toFixed(2),
        currency: "DHS"
      };

      // FormSubmit endpoint
const response = await fetch("https://formsubmit.co/ajax/aymanp", {
  method: "POST",
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    _subject: `New Order - ${formData.firstName} ${formData.lastName}`,
    _replyto: formData.email,
    ...orderDetails
  })
});

      if (response.ok) {
        setSubmissionMessage('Your order has been placed successfully! We will contact you shortly.');
      } else {
        const errorData = await response.json();
        setSubmissionMessage(`Submission failed: ${errorData.message || 'Server error'}`);
      }
    } catch (error) {
      console.error('Submission error:', error);
      setSubmissionMessage('There was an error submitting your order. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  useEffect(() => {
    // Redirect to cart if cart is empty, unless we are already on success page
    if (cart.length === 0 && !window.location.pathname.includes('/checkout/success')) {
      navigate('/cart');
    }
  }, [cart, navigate]);

  return (
    <>
      <div className='header'>
        <img src={logo} className="logo" alt="Logo"/>
        <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
          <li onClick={() => navigate('/')}>Home</li>
          <li onClick={() => navigate('/products')}>Products</li>
          <li onClick={() => navigate('/contact')}>Contact Us</li>
        </ul>
        <div className="header-right">
          <button
            className="continue-shopping-btn"
            onClick={() => navigate('/')}
          >
            Continue Shopping
          </button>

          {isMenuOpen ? (
            <FaTimes className="menu-icon" onClick={() => setIsMenuOpen(false)}/>
          ) : (
            <FaBars className="menu-icon" onClick={() => setIsMenuOpen(true)}/>
          )}
        </div>
      </div>

      <div className="checkout-page-container">
        <h2>Checkout</h2>

        <div className="checkout-content">
          <div className="customer-info-form">
            <h3>Shipping Information</h3>
            <form onSubmit={handleSubmit}> {/* Add onSubmit handler to the form */}
              <div className="form-group">
                <label htmlFor="firstName">First Name:</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  className={formErrors.firstName ? 'input-error' : ''}
                />
                {formErrors.firstName && <span className="error-text">{formErrors.firstName}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Last Name:</label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  className={formErrors.lastName ? 'input-error' : ''}
                />
                {formErrors.lastName && <span className="error-text">{formErrors.lastName}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={formErrors.email ? 'input-error' : ''}
                />
                {formErrors.email && <span className="error-text">{formErrors.email}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="phone">Phone Number:</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  className={formErrors.phone ? 'input-error' : ''}
                />
                {formErrors.phone && <span className="error-text">{formErrors.phone}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="address">Address:</label>
                <input
                  type="text"
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  className={formErrors.address ? 'input-error' : ''}
                />
                {formErrors.address && <span className="error-text">{formErrors.address}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="city">City:</label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  className={formErrors.city ? 'input-error' : ''}
                />
                {formErrors.city && <span className="error-text">{formErrors.city}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="zipCode">Zip Code:</label>
                <input
                  type="text"
                  id="zipCode"
                  name="zipCode"
                  value={formData.zipCode}
                  onChange={handleChange}
                  className={formErrors.zipCode ? 'input-error' : ''}
                />
                {formErrors.zipCode && <span className="error-text">{formErrors.zipCode}</span>}
              </div>
              {/* The Place Order button will be outside this form, but still trigger handleSubmit */}
            </form>
          </div>

          <div className="order-summary-and-payment">
            <h3>Order Summary</h3>
            <div className="summary-details">
              {cart.map(item => (
                <div key={`${item.id}-${item.selectedSize}-${item.selectedColor}`} className="summary-item">
                  <span>{item.title} ({item.quantity})</span>
                  {item.selectedSize && <span> - Size: {item.selectedSize}</span>}
                  {item.selectedColor && <span> - Color: {item.selectedColor}</span>}
                  <span>{(parseFloat(item.price) * item.quantity).toFixed(2)} DHS</span>
                </div>
              ))}
              <hr />
              <div className="summary-row">
                <span>Subtotal:</span>
                <span>{subtotal.toFixed(2)} DHS</span>
              </div>
              <div className="summary-row">
                <span>Shipping:</span>
                <span>{shippingCost.toFixed(2)} DHS</span>
              </div>
              <div className="summary-row total">
                <span>Total:</span>
                <span>{total.toFixed(2)} DHS</span>
              </div>
            </div>

            <div className="order-actions">
                {submissionMessage && <p className={submissionMessage.includes('successfully') ? 'success-message' : 'error-message'}>{submissionMessage}</p>}

                {cart.length === 0 ? (
                    <p className="error-message">Your cart is empty. Please add items to proceed.</p>
                ) : (
                    <>
                        {!isFormValid && (
                            <p className="fill-form-message">Please fill in all shipping information to place your order.</p>
                        )}
                        <button
                            type="submit"
                            className="submit-order-btn"
                            onClick={handleSubmit}
                            disabled={!isFormValid || isSubmitting || cart.length === 0}
                        >
                            {isSubmitting ? 'Placing Order...' : 'Place Order'}
                        </button>
                    </>
                )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
