import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

// Component imports with error handling
const loadComponent = (path, name) => {
  try {
    return require(`${path}`).default;
  } catch (error) {
    console.error(`Failed to load ${name}:`, error);
    return () => <div>Error loading {name} component</div>;
  }
};

const LandingPage = loadComponent("./landing_page", "LandingPage");
const CartPage = loadComponent("./CartPage", "CartPage");
const ProductPage = loadComponent("./ProductPage", "ProductPage");
const ProductsPage = loadComponent("./products_page", "ProductsPage");
const ContactPage = loadComponent("./ContactPage", "ContactPage");
const CheckoutPage = loadComponent("./CheckoutPage", "CheckoutPage");
// const CheckoutSuccess = loadComponent("./CheckoutSuccess", "CheckoutSuccess");

// Cart utilities with fallbacks
const loadCartFromLocalStorage = () => {
  try {
    const cart = localStorage.getItem('moroToteCart');
    return cart ? JSON.parse(cart) : [];
  } catch (error) {
    console.error("Failed to load cart from localStorage:", error);
    return [];
  }
};

const saveCartToLocalStorage = (cart) => {
  try {
    localStorage.setItem('moroToteCart', JSON.stringify(cart));
  } catch (error) {
    console.error("Failed to save cart to localStorage:", error);
  }
};

function App() {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const SHIPPING_COST = 0;

  // Initialize cart from localStorage
  useEffect(() => {
    const initializeCart = async () => {
      try {
        const loadedCart = loadCartFromLocalStorage();
        if (loadedCart && Array.isArray(loadedCart)) {
          setCart(loadedCart);
        }
      } catch (err) {
        console.error("Cart initialization error:", err);
        setError("Failed to initialize shopping cart");
      } finally {
        setLoading(false);
      }
    };

    initializeCart();
  }, []);

  // Persist cart to localStorage
  useEffect(() => {
    if (!loading) {
      saveCartToLocalStorage(cart);
    }
  }, [cart, loading]);

  const addToCart = (product, selectedSize = null, selectedColor = null) => {
    try {
      setCart((prevCart) => {
        const requiresOptions =
          product.product_type === "TSHIRT" ||
          (product.images && product.images.some((img) => img.color));

        const existingItem = prevCart.find(
          (item) =>
            item.id === product.id &&
            (!requiresOptions ||
              (item.selectedSize === selectedSize &&
                item.selectedColor === selectedColor))
        );

        return existingItem
          ? prevCart.map((item) =>
              item.id === existingItem.id
                ? {
                    ...item,
                    quantity: item.quantity + 1,
                    selectedSize: selectedSize || item.selectedSize,
                    selectedColor: selectedColor || item.selectedColor,
                  }
                : item
            )
          : [
              ...prevCart,
              {
                ...product,
                quantity: 1,
                selectedSize,
                selectedColor,
              },
            ];
      });
    } catch (err) {
      console.error("Failed to add to cart:", err);
      setError("Failed to add item to cart");
    }
  };

  const updateQuantity = (
    productId,
    newQuantity,
    selectedSize = null,
    selectedColor = null
  ) => {
    try {
      if (newQuantity < 1) {
        removeFromCart(productId, selectedSize, selectedColor);
        return;
      }

      setCart((prevCart) =>
        prevCart.map((item) =>
          item.id === productId &&
          item.selectedSize === selectedSize &&
          item.selectedColor === selectedColor
            ? { ...item, quantity: newQuantity }
            : item
        )
      );
    } catch (err) {
      console.error("Failed to update quantity:", err);
      setError("Failed to update item quantity");
    }
  };

  const removeFromCart = (
    productId,
    selectedSize = null,
    selectedColor = null
  ) => {
    try {
      setCart((prevCart) =>
        prevCart.filter(
          (item) =>
            !(
              item.id === productId &&
              item.selectedSize === selectedSize &&
              item.selectedColor === selectedColor
            )
        )
      );
    } catch (err) {
      console.error("Failed to remove from cart:", err);
      setError("Failed to remove item from cart");
    }
  };

  const updateCartItemOptions = (productId, { size, color }) => {
    try {
      setCart((prevCart) =>
        prevCart.map((item) =>
          item.id === productId
            ? {
                ...item,
                selectedSize: size !== undefined ? size : item.selectedSize,
                selectedColor: color !== undefined ? color : item.selectedColor,
              }
            : item
        )
      );
    } catch (err) {
      console.error("Failed to update item options:", err);
      setError("Failed to update product options");
    }
  };

  const calculateSubtotal = () => {
    try {
      return cart.reduce((total, item) => {
        const price = typeof item.price === "string" ? parseFloat(item.price) : item.price;
        return total + price * item.quantity;
      }, 0);
    } catch {
      return 0;
    }
  };

  const subtotal = calculateSubtotal();
  const total = subtotal + SHIPPING_COST;

  if (loading) {
    return (
      <div className="app-loading">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app-error">
        <div className="alert alert-danger">
          <h4>Application Error</h4>
          <p>{error}</p>
          <button
            className="btn btn-primary"
            onClick={() => window.location.reload()}
          >
            Reload Page
          </button>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="app-container">
        <main className="app-main">
          <Routes>
            <Route
              path="/"
              element={<LandingPage cart={cart} addToCart={addToCart} />}
            />
            <Route
              path="/products"
              element={<ProductsPage cart={cart} addToCart={addToCart} />}
            />
            <Route
              path="/cart"
              element={
                <CartPage
                  cart={cart}
                  subtotal={subtotal}
                  shippingCost={SHIPPING_COST}
                  updateQuantity={updateQuantity}
                  removeFromCart={removeFromCart}
                  updateCartItemOptions={updateCartItemOptions}
                />
              }
            />
            <Route
              path="/products/:id"
              element={<ProductPage addToCart={addToCart} cart={cart} />}
            />
            <Route path="/contact" element={<ContactPage />} />
            <Route
              path="/checkout"
              element={
                <CheckoutPage
                  cart={cart}
                  subtotal={subtotal}
                  shippingCost={SHIPPING_COST}
                  total={total}
                  setCart={setCart}
                />
              }
            />
            {/* <Route path="/checkout/success" element={<CheckoutSuccess />} /> */}
          </Routes>
        </main>
        <footer className="app-footer">
          <p className="text-center">
            &copy; {new Date().getFullYear()} MoroTote. All rights reserved.
          </p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
